DOCKER_VM_IP=`docker-machine ip realtime-dependencies`


if [ "$?" != "0" ]; then
    DOCKER_VM_IP=''
fi

if [ "$DOCKER_VM_IP" != "" ]; then
    echo "Docker VM is running."
    echo "Docker VM IP Address: $DOCKER_VM_IP"
else
    echo "Docker VM is not running."
    docker-machine start realtime-dependencies
fi

export KAFKA_ADVERTISED_HOST_NAME=$(docker-machine ip realtime-dependencies 2> /dev/null)

eval "$(docker-machine env realtime-dependencies)"
docker-compose up -d
docker-compose logs